"use strict";

$(document).on('ready', function() {
    "use strict";

    // ============================================================
    // Solución al conflicto de CORS
    // ============================================================
    // El Intercambio de Recursos de Origen Cruzado (CORS)
    // Si se configura en el web.config del sitio ASP.NET remoto con 
    // los webServices, entonces NO ES NECESARIO CONFIGURARLO AQUI.
    $.ajaxSetup({
        cache: false,
        timeout: $appConfig.ajaxCallTimeOut,
        async: true
        // async (por defecto: true): Por defecto todas las peticiones son configuradas para 
        // que sean asíncronas; lo que significa que el resto del JavaScript 
        // sigue ejecutándose sin importar si termino de procesar la petición.
        // En modo síncrono (async: false) la petición al servidor se realizaría de uno
        // En modo asíncrono (async: true) la petición al servidor se realizaría de manera paralela
    });

    window.oncontextmenu = function() { return !1 };
});